import React from 'react';
import {Link} from 'react-router-dom';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Paper from '@material-ui/core/Paper';
import withStyles from '@material-ui/core/styles/withStyles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import styles from './styles';
import '../index.js';
import firebase from 'firebase';

var firebaseConfig = {
  apiKey: "AIzaSyDH3NbjBcq0lWolH0hgki9E68NdCyXr7uk",
  authDomain: "chats-e59f7.firebaseapp.com",
  projectId: "chats-e59f7",
  storageBucket: "chats-e59f7.appspot.com",
  messagingSenderId: "525641251497",
  appId: "1:525641251497:web:f4e15b4fc7e5ef553607b8",
  measurementId: "G-JH4Z71BZVC"
};
 
var fire = firebase.initializeApp(firebaseConfig);

var defaultStorage = fire.storage();
var defaultFirestore = fire.firestore();

class SignupComponent extends React.Component{

  constructor() {
    super();
    this.state = {
      email: null,
      password: null,
      passwordConfirmation: null,
      signupError: ''
    };
  }

     render(){
        const {classes} = this.props;
          return(
        <main className = {classes.main}>
            <CssBaseline />
            <Paper className ={classes.paper}>
              <Typography component='h1' variant="h5">
                Sign Up
              </Typography>
              <form onSubmit={ (e) => this.submitSignup(e)} className = {classes.form}>
                 <FormControl required fullWidth margin='normal'>
                 <InputLabel htmlFor='signup-email-input'>Enter Your Email</InputLabel>
                 <Input  autoComplete='email' onChange={(e) => this.userTyping('email' ,e)} autoFocus id ='signup-email-input'></Input>
                 </FormControl>
                 <FormControl required fullWidth margin='normal'>
                   <InputLabel htmlFor='signup-password-input'>Create A Password</InputLabel>
                   <Input type='password'  onChange={(e) => this.userTyping('password' ,e)} id ='signup-password-input'></Input>
                 </FormControl>
                 <FormControl required fullWidth margin='normal'>
                   <InputLabel htmlFor='signup-password-confirmation-input'>Confirm Your Password</InputLabel>
                   <Input type='password'  onChange={(e) => this.userTyping('passwordConfirmation' ,e)} id ='signup-password-confirmation-input'></Input>
                 </FormControl>
                 <Button type='submit' fullWidth variant = 'contained' color='primary' className ={classes.submit}>Submit</Button>
                 </form>
                 { 
                this.state.signupError ? 
                <Typography className={classes.errorText} component='h5' variant='h6'>
                 {this.state.signupError}
                  </Typography> :
                    null
          }
                 <Typography compenent='h5' variant='h6' className={classes.hasAccountHeader}>Already Have An Account?</Typography>
                 <Link className={classes.logInLink} to="/login">Log In!</Link>
            </Paper>
        </main>
          );
     }
     
     

     userTyping = (type, e) =>{
        switch (type) {
          case 'email':
            this.setState({email: e.target.value});
            break;
        
         case 'password':
           this.setState({password: e.target.value});
             break;
          case 'passwordConfirmation':
            this.setState({passwordConfirmation: e.target.value});
            break;
        
          
          default:
            break;
        }
     }
     formidIsValid = () =>this.state.password===this.state.passwordConfirmation;
     submitSignup = (e) =>{
       e.preventDefault();
       if(!this.formidIsValid()){
         this.setState({signupError:'Password do not match'});
         return;
       }
       firebase
       .auth()
       .createUserWithEmailAndPassword(this.state.email, this.state.password)
       .then(authRes => {
         const userObj = {
         email: authRes.user.email
         };
         firebase
         .firebase()
         .doc(this.state.email)
         .set(userObj)
         .collection('user')
         .then(() => {
            this.props.history.push('/dashboard')
          }, dbError =>{
            console.log(dbError);
            this.setState({signupError : 'Failed to add user'});
          })
        },authErr =>{
          console.log(authErr);
          this.setState({signupError : 'Failed to add user'});
        })
     }
}
export default withStyles(styles)(SignupComponent);
